import React, {useState, useRef} from "react";
import {SeatButton} from "../styles";

export default function Seats({indexedSeat,rowNo,handleSeatSelection,isSelectedArr}) {
    return (
        <SeatButton isSelected={isSelectedArr[rowNo] && isSelectedArr[rowNo][indexedSeat]} onClick={handleSeatSelection.bind(null,rowNo,indexedSeat)} >
          {indexedSeat}
        </SeatButton>
    );
}
