import React, {useState, useRef} from "react";
import Seats from "./seats";

export default function SeatList({ns, rowNo}) {
    const noOfSeats = Array.from(Array(ns).keys());
    const isSelectedArr = [[]];

    const handleSeatSelection = (RindexedSeat, RrowNo, e) => {
        if (!isSelectedArr[RrowNo]) {

            isSelectedArr[RrowNo] = [];
            isSelectedArr[RrowNo][RindexedSeat] = true;

        }

    };
    return (
        <div>
            rowNo:{rowNo}
            {noOfSeats?.map((val, index) => {
                return <Seats rowNo={rowNo} isSelectedArr={isSelectedArr} indexedSeat={index}
                              handleSeatSelection={handleSeatSelection}/>
            })}
        </div>
    );
}
