import React, {useState, useRef} from "react";
import SeatList from "../seatList";

export default function Clubs({label, totalSeats, seatInARow, maxSelection}) {

    const noOfRows = Math.floor(totalSeats / seatInARow);

    const totalRows = Array.from(Array(noOfRows).keys());
    const isSelectedArr = {
        [label]: [[]]
    };

    return (
        <div>
            {label}
            {totalRows?.map((value => {
                return <SeatList isSelectedArr={isSelectedArr[label]} ns={seatInARow} rowNo={value}/>
            }))}
        </div>
    );
}
